    // Styling for the Synapse-specific Draft.js
    // Editor and Toolbar Components

// **************************************************
// **************************************************

.DraftJSEditor-root {
    background: #fff;
    border: 1px solid #ddd;
    font-family: inherit;
    font-size: 14px;
    padding: 15px;
}

.DraftJSEditor-editor {
    border-top: 1px solid #ddd;
    cursor: text;
    font-size: 16px;
    margin-top: 10px;
}

.DraftJSEditor-editor .public-DraftEditorPlaceholder-root,
.DraftJSEditor-editor .public-DraftEditor-content {
    margin: 0 -15px -15px;
    padding: 15px;
}

.DraftJSEditor-editor .public-DraftEditor-content {
    min-height: 100px;
}

.DraftJSEditor-hidePlaceholder .public-DraftEditorPlaceholder-root {
    display: none;
}

.DraftJSEditor-controls {
    font-family: inherit, sans-serif;
    font-size: 14px;
    margin-bottom: 5px;
    user-select: none;
}

.DraftJSEditor-styleButton {
    color: #999;
    fill: #999;
    cursor: pointer;
    margin-right: 16px;
    padding: 2px 0;
    display: inline-block;
}

.DraftJSEditor-activeButton {
    color: #5890ff;
    fill: #5890ff;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

html {
