select:-webkit-autofill:hover,
select:-webkit-autofill:focus {
  border: 1px solid #364375;
  -webkit-text-fill-color: #364375;
  -webkit-box-shadow: 0 0 0px 1000px #fff inset;
  transition: background-color 5000s ease-in-out 0s;
}

.md-panel-content .md-text-field-container {
  padding-left: 0;
  padding-right: 0;
}

.destination-content {
  display: flex;
  flex-wrap: wrap;
}

.destinations {
  display: flex;
  flex-wrap: wrap;

  .md-chip-container {
    margin: .5em;
  }
}

.align-end {
  align-self: flex-end;
}

.dot-overflow {
  display: block;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.flex-between {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;

  .md-picker-container {
    flex-grow: 1;
    flex-shrink: 0;
    margin: 1em;
    min-width: 240px;
  }
}

.header__icon {
