$md-bottom-nav-shifting-inactive-min-width: 56px;

/// The max width for an inactive bottom-nav action when the bottom-nav size is
/// shifting. A shifting bottom-nav is used when there are more than 3 actions.
///
/// @type String
/// @group bottom-navigations
$md-bottom-nav-shifting-inactive-max-width: 96px;

/// The padding to use for a bottom-nav action.
///
/// @type String
/// @group bottom-navigations
$md-bottom-nav-padding: 6px 12px 10px;

/// The font size to use for an active bottom-nav action.
///
/// @type String
/// @group bottom-navigations
$md-bottom-nav-active-font-size: 14px;

/// The font size to use for an inactive bottom-nav action.
///
/// @type String
/// @group bottom-navigations
$md-bottom-nav-inactive-font-size: 12px;

/// The transition time for the label appearing in a shifting bottom-nav action.
///
/// @type Time
/// @group bottom-navigations
$md-bottom-nav-transition-time: $md-transition-time !default;

/// The transition time for the entire bottom-navigation component appearing when
/// a user has scrolled the bottom-navigation into or out of view.
///
/// @type Time
/// @group bottom-navigations
$md-bottom-navigation-transition-time: $md-transition-time !default;


