  $padding: ($md-font-icon-size - $md-data-table-sort-icon-font-size) / 2;

  .md-icon {
    font-size: $md-data-table-sort-icon-font-size;
    padding: $padding 0;
    transition-duration: $md-transition-time;
    transition-property: color, transform;
  }

  .inline-top {
    display: inline-block;
    padding: $padding;
