    padding: $md-data-table-padding;
    padding-bottom: 0;
    top: 0;
  }

  .md-text-field-container {
    @extend %full-width;

    padding: 0;
  }

  .md-text-field {
    @extend %full-width;

    &:hover {
      cursor: pointer;
    }

    &.active:hover {
      cursor: initial;
    }
  }

  .md-title {
    margin: 0;
