    z-index: $md-dialog-z-index;
  }

  &.dialog-centered {
    align-items: center;
    display: flex;
    justify-content: center;
    padding: $md-dialog-min-page-margin;
  }

  .md-overlay {
    background: $md-dialog-overlay-color;
    z-index: -1;
  }

  .md-overlay-leave.md-overlay-leave-active {
