.md-ink {
  background: $md-divider-color;
  border-radius: 50%;
  display: block;
  opacity: 1;
  position: absolute;
  transform: scale(0);
  z-index: -1;

  &.active {
    transform: scale(2.5);
    transition-duration: $md-ink-animation-time;
    transition-property: opacity, transform;
    transition-timing-function: $md-ink-transition-func;

    &.leaving {
      opacity: 0;
    }
  }
