.md-list-tile {
  align-items: center;
  color: $md-text-color;
  display: flex;
  overflow: hidden;
  padding-left: $md-default-padding;
  padding-right: $md-default-padding;
  position: relative;
  transition: background $md-transition-time;

  &:focus {
    outline: none;
