@include md-list-nested-offset($md-list-max-nested-items, $md-list-nested-increment);
/// Themes the list components with the given colors.
///
/// @param {String} $color - The primary color to use
/// @param {Boolean} $light-theme [$md-light-theme] - Boolean if it should be styled for
///           the light theme or the dark theme.
@mixin md-theme-lists($color, $light-theme: $md-light-theme) {
  .md-list {
    .md-subheader {
      color: if($light-theme, $md-light-theme-secondary-text-color, $md-dark-theme-secondary-text-color);

      &.md-primary-subheader {
        color: $color;
      }
    }
  }

  .md-list-tile {
    &.active,
    &.active .md-icon {
      color: $color;
    }
  }

  .md-list-tile-secondary-text {
    color: if($light-theme, $md-light-theme-secondary-text-color, $md-dark-theme-secondary-text-color);
  }
}

@mixin md-lists-mobile() {
  .md-list {
    padding-bottom: $md-list-mobile-top-padding;
    padding-top: $md-list-mobile-top-padding;
  }

  .md-menu .md-list {
    top: -$md-list-mobile-top-padding;
  }


  .md-list-tile,
  .md-list-item.primary-action,
  .md-list-item.secondary-action {
    height: $md-list-mobile-height;

    &.avatar-height {
      height: $md-list-mobile-avatar-height;
    }

    &.two-lines {
      height: $md-list-mobile-2-line-height;
    }

    &.three-lines {
      height: $md-list-mobile-3-line-height;

      .md-tile-secondary-text {
        // Allows two lines of text
        height: 36px;
      }
    }
  }

  .md-tile-primary-text {
    font-size: $md-list-mobile-font-size;
  }

  .md-tile-secondary-text {
    font-size: $md-list-mobile-secondary-text-font-size;
  }
}

@mixin md-lists-desktop() {
  .md-list {
    padding-bottom: $md-list-desktop-top-padding;
    padding-top: $md-list-desktop-top-padding;
  }

  .md-menu .md-list {
    top: -$md-list-desktop-top-padding;
  }

  .md-list-tile,
  .md-list-item.primary-action,
  .md-list-item.secondary-action {
    height: $md-list-desktop-height;

    &.avatar-height {
      height: $md-list-desktop-avatar-height;
    }

    &.two-lines {
      height: $md-list-desktop-2-line-height;
    }

    &.three-lines {
      height: $md-list-desktop-3-line-height;

      .md-tile-secondary-text {
        // Allows two lines of text
        height: $md-line-height * $md-list-desktop-font-size * 2;
      }
    }
  }

  .md-list-item:hover {
    cursor: pointer;
  }

  .md-list-tile:hover:not([disabled]) {
    background: if($md-light-theme, $md-light-theme-divider-color, $md-dark-theme-divider-color);
  }

  .md-tile-primary-text,
  .md-tile-secondary-text {
    font-size: $md-list-desktop-font-size;
  }
}

@mixin md-list-nested-offset($depth, $increment) {
  $selector: '+ .md-list .md-list-tile';
  $full-selector: '';
  $multiplier: 1;
  @for $i from 1 through $depth {
    $full-selector: '#{$full-selector} #{$selector}';

    .md-list-tile #{$full-selector} {
      padding-left: $md-list-tile-left-padding * $multiplier;
    }

    $multiplier: $multiplier + $increment;
  }
}
.md-menu-container {
  display: inline-block;
  position: relative;

  &.full-width {
    @extend %full-width;
    display: block;
  }
}

.md-menu {
  @include md-box-shadow(2);

  background: $md-white-base;
  position: absolute;
  top: 0;
  z-index: 28;

  &.limit-height {
    max-height: $md-dropdown-max-height;
    overflow-y: auto;
  }

  &.below {
    border-top: 1px solid $md-border-color;
    top: 100%;
    transform-origin: 0 0;
  }

  &.cascading {
    padding-bottom: 16px;
    padding-top: 16px;

    .md-list-tile {
      height: 32px;
      padding-left: 24px;
      padding-right: 24px;

      // scss-lint:disable NestingDepth
      &.secondary-action {
        padding-right: 24 - $md-default-padding;
      }
    }

    .md-tile-primary-text {
      font-size: 15px;
    }
  }

  &.full-width {
    @extend %full-width;
  }

  .md-menu-container {
    position: absolute;
  }

  .md-list-item {
    position: relative;

    .md-list {
      @include md-box-shadow(3);

      background: if($md-light-theme, $md-white-base, $md-dark-theme-background-color);
      left: 100%;
      position: absolute;
      z-index: 3;
    }
  }
}

.md-transition-tr {
  right: 0;
  transform-origin: 100% 0;
}

.md-transition-tl {
  transform-origin: 0 0;
}

.md-transition-br {
  right: 0;
  transform-origin: 100% 100%;
}

.md-transition-bl {
  transform-origin: 0 100%;
}

.md-transition-below {
  top: 100%;
  transform-origin: 50% 0;
  width: 100%;

  &.md-menu-enter {
    transform: scaleY(0);

    &.md-menu-enter-active {
      transform: scaleY(1);
    }
  }

  &.md-menu-leave {
    transform: scaleY(1);

    &.md-menu-leave-active {
      transform: scaleY(0);
    }
  }
}

.md-menu-enter {
  transform: scale(0);

  &.md-menu-enter-active {
    transform: scale(1);
    transition: transform $md-transition-time;
  }
}

.md-menu-leave {
  transform: scale(1);

  &.md-menu-leave-active {
    transform: scale(0);
    transition: transform $md-transition-time;
  }
}

.md-toolbar-container + .md-menu-container {
  display: block;

  .md-menu {
    width: 100%;
  }
}
.md-navigation-drawer {
  @include md-box-shadow(1);

  height: 100vh;
  left: 0;
  overflow-y: auto;
  position: fixed;
  z-index: $md-navigation-drawer-z-index;

  &.active,
  &.mini {
    z-index: $md-navigation-drawer-z-index - 5;
  }

  &.mobile {
    transition: transform $md-navigation-drawer-transition-time;
  }

  &.mobile-inactive {
    transform: translate3d(-100vw, 0, 0);
  }

  &.mobile-active {
    transform: translate3d(0, 0, 0);
  }

  &.mini {
    overflow-x: hidden;
    transition-duration: $md-navigation-drawer-transition-time;
    transition-property: width;
    width: $md-navigation-drawer-mini-width;

    .md-list-tile {
      justify-content: center;
    }

    &:not(.active) .md-tile-content {
      display: none;
      visibility: hidden;
    }
  }

  &.temporary.active {
    @include md-box-shadow(3);

    z-index: $md-navigation-drawer-temporary-z-index;
  }
}

.md-navigation-drawer-content {
  padding-left: 0;
  transition: padding-left $md-navigation-drawer-transition-time;

  &.mini {
    padding-left: $md-navigation-drawer-mini-width;
    position: relative;
  }
}

.md-navigation-drawer-toolbar,
.md-drawer-header {

  .md-title {
    display: inline-block;
    margin: 0;
    vertical-align: top;
  }
}

.md-drawer-header {
  .md-icon-btn + .md-title {
    padding-left: 12px;
  }

  .md-title {
    color: $md-secondary-text-color;
    padding-left: $md-default-padding;
  }
}

.md-navigation-drawer-toolbar {
  @include md-box-shadow(1);

  color: $md-white-base;
  left: 0;
  position: fixed;
  top: 0;
  transition-duration: $md-navigation-drawer-transition-time;
  transition-property: box-shadow, left, width;
  width: 100vw;
  z-index: $md-navigation-drawer-toolbar-z-index;

  .md-title {
    color: inherit;
    padding-left: $md-default-padding;
  }

  .md-text-field {
    font-size: $md-title-font-size;
  }

  .md-icon-btn {
    margin-left: $md-toolbar-action-margin;
    margin-right: $md-toolbar-action-margin;

    + .md-title {
      padding-left: 0;
    }
  }
}

.md-drawer-toggle {
  float: right;
}


.drawer-leave,
.drawer-enter.drawer-enter-active {
  transform: translate3d(0, 0, 0);
}

.drawer-enter-active,
.drawer-leave-active {
  transition: transform $md-navigation-drawer-transition-time;
}

.cross-fade-enter {
  opacity: .01;
  transform: translate3d(0, $md-cross-fade-distance, 0);

  &.cross-fade-enter-active {
    opacity: 1;
    transform: translate3d(0, 0, 0);
    transition-duration: $md-cross-fade-transition-time;
    transition-property: transform, opacity;
  }
}

.md-drawer-scrolling-list {
  overflow-y: auto;
}

.md-overlay {
  background: rgba($md-black-base, .4);
  height: 100vh;
  left: 0;
  position: fixed;
  top: 0;
  width: 100vw;
  z-index: $md-overlay-z-index;
}

.md-overlay-enter {
  opacity: 0;

  &.md-overlay-enter-active {
    opacity: 1;
    transition: opacity $md-transition-time;
  }
}

.md-overlay-leave {
  opacity: 1;

  &.md-overlay-leave-active {
    opacity: 0;
    transition: opacity $md-transition-time;
  }
}

%paper {
  background: $md-card-color;
  border-radius: 2px;
}


/// Makes paper at the specific z-depth
///
/// @example scss - Basic Usage Sass
///    .paper-2 {
///      @include make-paper(2);
///    }
///
/// If the depth is 0, a hover effect will be added to raise the card to z-depth of 3.
///
/// @param {Number} z-depth [1] - The zDepth for the card
@mixin make-paper($z-depth: 1) {
  @extend %paper;
  @include md-box-shadow($z-depth);

  @if $z-depth == 0 {
    transition: box-shadow $md-transition-time * 3;

    &:hover {
      @include md-box-shadow(3);
    }
  }
}

@for $i from 0 through 5 {
  .paper-#{$i} {
    @include make-paper($i)
  }
}
.md-picker-container {
  position: relative;

  .md-text-field:hover,
  .md-text-field-label:hover {
    cursor: pointer;
  }
}

.md-picker-control.active,
.md-date-picker-date.active,
.md-date-picker-year.active,
.md-calendar-date:not([disabled]):hover,
.md-calendar-date:not([disabled]):focus,
.md-calendar-date:not([disabled]).active {
  color: $md-dark-theme-text-color;
}

.md-picker-control,
.md-date-picker-date,
.md-date-picker-year {
  color: $md-dark-theme-secondary-text-color;
}

.md-picker {
  &.inline {
    @include md-box-shadow(3);

    position: absolute;
    z-index: 24;

    &.with-icon {
      left: 50px;
    }
  }

  &.portrait {
    @include md-pickers-portrait();
  }

  &.landscape {
    @include md-pickers-landscape();
  }

  &.date-picker {
    .md-picker-control {
      display: block;
    }
  }

  &.time-picker {
    .md-picker-header {
      text-align: right;
    }

    .md-picker-control {
      padding: 0;
    }
  }
}

.md-time-periods {

  .md-picker-control {
    display: block;
  }
}

.md-picker-header {
  padding: $md-dialog-padding;
}

.md-picker-control {
  @extend %button-unstyled;
  @extend %clickable;

  .md-subtitle {
    font-size: 16em / $md-font-size-base;
    font-weight: $md-font-medium;
    margin: 0;
    padding: 0;
  }

  .md-display-1,
  .md-display-3 {
    color: inherit;
    margin-bottom: 0;
  }

  .md-display-1 {
    font-weight: 500;
    text-align: left;
  }
}




.md-picker-content-container {
  overflow: hidden;
}


.md-picker-content.clock {
  padding: $md-dialog-padding;
  padding-bottom: $md-dialog-padding / 2;
}

.md-clock-face {
  border-radius: 50%;
  margin: auto;
  position: relative;

  &:hover {
    cursor: pointer;
  }
}

.md-clock-hand {
  height: 2px;
  position: absolute;
  transform-origin: 0 0;
  transition-duration: $md-transition-time;
  transition-property: width;
  z-index: 4;

  &.active {
    transition-property: transform, width;
  }

  &::before,
  &::after {
    border-radius: 50%;
    content: '';
    position: absolute;
  }

  // center ball
  &::before {
    height: $md-clock-hand-ball-size;
    left: -$md-clock-hand-ball-size / 2;
    // no idea why
    top: -2px;
    width: $md-clock-hand-ball-size;
  }

  &::after {
    height: $md-clock-time-size;
    right: -$md-clock-time-size / 2 + $md-clock-padding / 2;
    top: -$md-clock-time-size / 2;
    transition-duration: $md-transition-time;
    transition-property: transform, background, border;
    width: $md-clock-time-size;
  }

  &.invisible-minute::after {
    transform: scale($md-clock-hand-ball-scale);
  }
}

// Prevents the event.target to be anything other than the .md-clock-face
.md-clock-time,
.md-clock-hand {
  pointer-events: none;
  user-select: none;
}

.md-clock-time {
  display: table;
  height: $md-clock-time-size;
  position: absolute;
  transition: color $md-transition-time;
  width: $md-clock-time-size;
  z-index: 8;
}

.md-clock-time-value {
  display: table-cell;
  font-size: $md-clock-time-font-size;
  text-align: center;
  vertical-align: middle;
}



.md-calendar {
  position: relative;
}

.md-calendar-controls {
  .md-subtitle {
    color: $md-text-color;
    line-height: $md-font-icon-size;
    margin: 0;
    padding: $md-dialog-padding / 2;
    text-align: center;
  }

  .md-icon-btn {
    color: $md-text-color;
    position: absolute;
    top: 0;

    &:first-of-type {
      left: 12px;
    }

    &:last-of-type {
      right: 12px;
    }
  }
}

.md-calendar-month,
.md-dows {
  padding-left: $md-dialog-padding / 2;
  padding-right: $md-dialog-padding / 2;
}

.md-dows {
  .dow {
    color: $md-hint-text-color;
  }
}

.md-dows .dow,
.md-calendar-date-placeholder,
.md-calendar-date {
  display: inline-block;
  margin: 0;
  text-align: center;
  vertical-align: top;
  width: calc(100% / 7);
}

.md-dows .dow,
.md-calendar-date {
  font-size: 13px;
}

.md-calendar-date {
  @extend %button-unstyled;
  @extend %clickable;

  color: $md-text-color;
  position: relative;
  transition: color $md-transition-time;

  .date {
    position: relative;
    z-index: 1;
  }

  &[disabled] {
    color: $md-disabled-color;
  }

  &.active,
  &.today {
    font-weight: $md-font-bold;
  }

  &:not([disabled]) {
    &:hover,
    &:focus,
    &.active {
      //scss-lint:disable NestingDepth
      &::after {
        transform: translateX(-50%) translateY(-50%) scale(1);
      }
    }
  }

  &::after {
    border-radius: 50%;
    content: '';
    display: block;
    left: 50%;
    position: absolute;
    top: 50%;
    transform: translateX(-50%) translateY(-50%) scale(0);
    transition: transform $md-date-picker-transition-time;
    z-index: 0;
  }
}

.md-year-picker {
  overflow-y: auto;
}

.md-years {
  list-style: none;
  margin: 0;
  padding: 0;
}

.md-year {
  @extend %button-unstyled;
  @extend %clickable;

  display: block;
  font-size: $md-year-picker-font-size;
  padding: 12px;
  text-align: center;
  transition-duration: $md-transition-time;
  transition-property: color, font-size;
  width: 100%;

  &.active {
    font-size: $md-year-picker-active-font-size;
    font-weight: bold;
  }

  &:focus:not(.active) {
    font-size: $md-year-picker-focus-font-size;
  }
}
/// Styles the date picker and the time picker to a portrait display.
///
/// @example scss - Basic Usage SASS
///   .md-picker.portrait {
///     @include md-pickers-portrait;
///   }
///
/// @group pickers
@mixin md-pickers-portrait() {
  width: $md-pickers-portrait-width;

  &.date-picker {

    .md-display-1 {
      display: inline-block;
    }
  }

  .md-picker-header {
    height: $md-pickers-portrait-header-height;
  }

  .md-time-periods {
    display: inline-block;
    padding-left: 1em;
    padding-right: $md-dialog-padding;
  }

  .md-picker-content {
    height: $md-pickers-portrait-content-height;
  }

  .md-display-3 {
    font-size: 4.8em;
    line-height: 1;
  }

  .md-dows {
    padding-bottom: $md-dialog-padding / 2;
  }

  .md-calendar-date-placeholder,
  .md-calendar-date {
    height: $md-date-picker-portrait-date-size;
  }

  .md-calendar-date::after {
    height: $md-date-picker-portrait-ball-size;
    width: $md-date-picker-portrait-ball-size;
  }

  .md-clock-face {
    height: $md-clock-portrait-size;
    width: $md-clock-portrait-size;
  }

  .md-clock-hand {
    width: ($md-clock-portrait-size - $md-clock-padding - $md-clock-time-size) / 2;

    &.inner-hour {
      width: ($md-clock-portrait-size - $md-clock-padding - $md-clock-time-size * 3) / 2;
    }
  }
}

/// Styles the date picker and the time picker to a landscape display.
///
/// @example scss - Basic Usage SASS
///   .md-picker.landscape {
///     @include md-pickers-portrait;
///   }
///
/// @group pickers
@mixin md-pickers-landscape() {
  display: flex;
  width: $md-pickers-landscape-width;

  .md-picker-header {
    width: $md-pickers-landscape-header-width;
  }

  .md-picker-content {
    height: $md-pickers-landscape-content-height;
    width: $md-pickers-landscape-width - $md-pickers-landscape-header-width;
  }

  .md-calendar-date-placeholder,
  .md-calendar-date {
    height: $md-date-picker-landscape-date-size;
  }

  .md-calendar-date::after {
    height: $md-date-picker-landscape-ball-size;
    width: $md-date-picker-landscape-ball-size;
  }


  &.time-picker .md-picker-header {
    padding-top: $md-pickers-landscape-content-height / 3;
  }

  .md-clock-face {
    height: $md-clock-landscape-size;
    width: $md-clock-landscape-size;
  }

  .md-clock-hand {
    width: ($md-clock-landscape-size - $md-clock-padding - $md-clock-time-size) / 2;

    &.inner-hour {
      width: ($md-clock-landscape-size - $md-clock-padding - $md-clock-time-size * 3) / 2;
    }
  }

  .md-time-periods {
    margin-left: auto;
    margin-right: 2.5em;
    width: 35px;
  }

  .md-display-3 {
    font-size: 3.25em;
  }
}

/// Themes a date and time picker with the given colors
///
/// @example scss - Basic Usage SASS
///   .my-primary-theme {
///     @inlude md-theme-date-pickers($md-indigo-500, $md-indigo-400);
///   }
///
/// @example scss - Basic Usage Output SCSS
///   .my-primary-theme {
///     .md-picker.inline {
///       background: #fff;
///     }
///
///     .md-picker-header,
///     .md-date-picker-header,
///     .md-calendar-date.active::after,
///     .md-clock-hand,
///     .md-clock-hand::before,
///     .md-clock-hand::after {
///       background: #3f51b5;
///     }
///
///     .md-calendar-date.today:not(.active),
///     .md-year.active {
///        color: #3f51b5;
///     }
///
///     .md-calendar-date::after {
///       background: #5c6bc0;
///     }
///
///     .md-year:focus:not(.active) {
///       color: #5c6bc0;
///     }
///
///     .md-clock-face {
///       background: #e0e0e0;
///     }
///
///     .md-clock-hand.invisible-minute::after {
///       background: #fff;
///       border: 6px solid $primary;
///     }
///
///     .md-clock-time {
///       color: #000;
///     }
///
///     .md-clock-time.active {
///       color: #fff;
///     }
///
/// @param {Color} $primary - The primary color to use.
/// @param {Color} $hover-color - The hover color to use.
/// @param {Boolean} $light-theme [$md-light-theme] - Boolean if it is the light theme
/// @group pickers
@mixin md-theme-pickers($primary, $hover-color, $light-theme: $md-light-theme) {
  .md-picker.inline {
    background: if($light-theme, $md-light-theme-card-color, $md-dark-theme-card-color);
  }

  .md-picker-header,
  .md-date-picker-header,
  .md-calendar-date.active::after,
  .md-clock-hand,
  .md-clock-hand::before,
  .md-clock-hand::after {
    background: $primary;
  }

  .md-calendar-date.today:not(.active),
  .md-year.active {
    color: $primary;
  }

  .md-calendar-date::after {
    background: $hover-color;
  }

  .md-year:focus:not(.active) {
    color: $hover-color;
  }

  .md-clock-face {
    background: if($light-theme, $md-grey-300, $md-grey-800);
  }

  .md-clock-hand.invisible-minute::after {
    background: $md-white-base;
    border: $md-clock-hand-ball-border-size solid $primary;
  }

  .md-clock-time {
    color: if($light-theme, $md-black-base, $md-white-base);

    &.active {
      color: if($light-theme, $md-white-base, $md-black-base);
    }
  }
}
@keyframes md-linear-indeterminate {
  0% {
    left: -35%;
    right: 100%;
  }

  60% {
    left: 100%;
    right: -90%;
  }

  100% {
    left: 100%;
    right: -90%;
  }
}

@keyframes md-linear-indeterminate-short {
  0% {
    left: -200%;
    right: 100%;
  }

  60% {
    left: 107%;
    right: -8%;
  }

  100% {
    left: 107%;
    right: -8%;
  }
}

@keyframes md-circular-progress {
  0% { transform: rotate(0deg); }

  50% { transform: rotate(135deg); }

  75% { transform: rotate(450deg); }

  100% { transform: rotate(720deg); }
}

@keyframes md-circular-progress-dash {
  0% {
    stroke-dashoffset: $md-circular-progress-stroke-dashoffset;
  }

  50% {
    stroke-dashoffset: $md-circular-progress-stroke-dashoffset / 4;
  }

  100% {
    stroke-dashoffset: $md-circular-progress-stroke-dashoffset;
  }
}

.md-linear-progress.determinate,
.md-linear-progress-container {
  display: block;
  height: $md-linear-progress-height;
}

.md-linear-progress-container {
  @extend %full-width;

  margin: 1em 0;
  overflow: hidden;
  position: relative;
}

.md-linear-progress {

  &.determinate {
    position: absolute;
    transition: width $md-transition-time;
    z-index: 1;
  }

  &.indeterminate {
    &::before,
    &::after {
      background: inherit;
      bottom: 0;
      content: '';
      position: absolute;
      top: 0;
      will-change: left, right;
      z-index: 1;
    }

    &::before {
      animation-duration: $md-linear-progress-anim-time;
      animation-iteration-count: infinite;
      animation-name: md-linear-indeterminate;
      animation-timing-function: $md-linear-progress-long-transition-func;
    }

    &::after {
      animation-delay: $md-linear-progress-short-delay;
      animation-duration: $md-linear-progress-anim-time;
      animation-iteration-count: infinite;
      animation-name: md-linear-indeterminate-short;
      animation-timing-function: $md-linear-progress-short-transition-func;
    }

    &.query::before,
    &.query::after {
      animation-direction: reverse;
    }
  }
}

.md-circular-progress {
  display: block;
  margin: 1em;

  &.centered {
    margin: 1em auto;
  }

  &.determinate {
    transition: transform $md-transition-time;

    .md-circular-progress-path {
      stroke-dasharray: $md-circular-progress-determinate-dasharray;
    }
  }

  &.indeterminate {
    animation: md-circular-progress $md-circular-progress-duration linear infinite;

    .md-circular-progress-path {
      animation: md-circular-progress-dash $md-circular-progress-duration $md-circular-progress-transition-func infinite;
      stroke-dasharray: $md-circular-progress-stroke-dashoffset;
      stroke-dashoffset: 0;
    }
  }
}

.md-circular-progress-path {
  fill: none;
}
.md-select-field-menu {
