    margin: 0;
  }

  .md-flat-btn {
    min-width: initial;
    overflow: visible;
  }
}

.snackbar-enter-active:not(:only-child),
.snackbar-enter-active:not(:only-child) * {
  transition-delay: $md-snackbar-chained-transition-delay;
}

.snackbar-enter-active,
.snackbar-leave-active {
