  left: 0;
  position: absolute;
  transition-duration: $md-tab-indicator-transition-time;
  transition-property: left, width;
}

.md-tab {
  flex-shrink: 0;
  font-weight: $md-font-medium;
  overflow: hidden;
  position: relative;
  text-align: center;
  text-transform: uppercase;
  transition: color $md-transition-time;

  &.active {
    color: $md-primary-color;
  }

  &:hover {
    cursor: pointer;
  }

  &:focus {
