  }
}


.md-tab-label {
  align-items: center;
  cursor: inherit;
  display: flex;
  flex-direction: column;
  height: $md-tab-height;
  justify-content: center;
  max-width: $md-tab-max-width;
  padding: $md-tab-padding;

  &.multiline {
    font-size: $md-tab-multiline-font-size;
  }

  &:not(.multiline) {
    padding-bottom: $md-tab-single-line-padding-bottom;
  }


  &.with-icon {
    height: $md-tab-height-icon;
    padding-bottom: $md-tab-with-icon-padding-bottom;

    .md-icon {
      margin-bottom: $md-tab-icon-padding;
    }
  }
}

.md-tab-control {
  @extend %hidden;
}

.md-tab-content-container .md-swipeable-item:not(.swiping) {
  transition-duration: $md-tab-indicator-transition-time;
}
.md-text-field-container {
  @include md-theme-placeholders($md-secondary-text-color);
  display: inline-block;
  vertical-align: top;

  &.block {
    align-items: center;
    display: flex;

    &.multi-line {
      align-items: flex-start;
      flex-wrap: wrap;
      justify-content: flex-end;

      // scss-lint:disable NestingDepth
      .md-text-field-message {
        width: 100%;
      }
    }

    .md-text-field-label {
      flex-grow: 1;
    }

    .md-text-field-message {
      flex-shrink: 0;
    }
  }

  &.with-message {
    width: $md-text-field-info-width;

    .md-text-field {
      width: 100%;
    }
  }

  &.full-width {
    width: 100%;
  }

  &.disabled {
    @extend %md-disabled-text;
    @include md-theme-placeholders($md-disabled-color);
  }

  // scss-lint:disable VendorPrefix, PseudoElement
  :-moz-placeholder {
    opacity: 1;
  }

  ::-moz-placeholder {
    opacity: 1;
  }

  :-ms-input-placeholder {
    opacity: 1;
  }

  ::-webkit-input-placeholder {
    opacity: 1;
  }
}

.md-text-field-label {
  display: block;
  position: relative;
  white-space: nowrap;
}

.md-text-field:not(.multi-line),
.md-floating-label {
  line-height: 1;
}

.md-text-field {
  background: none;
  border: 0;
  display: inline-block;
  padding-top: $md-text-field-floating-label-top;
  vertical-align: top;

  &:focus {
    outline: none;
  }

  &:-webkit-autofill,
  &:-webkit-autofill:focus {
    box-shadow: 0 0 0 50px $md-white-base inset;
  }

  &.multi-line {
    margin-top: $md-text-field-floating-label-top;
    padding-bottom: 0;
    padding-top: 0;
    resize: none;
    transition-duration: $md-transition-time;
    transition-property: height;
    width: 100%;
  }

  &.block {
    width: 100%;
  }

  &.full-width {
    width: 100%;

    &.with-icon {
      width: calc(100% - #{$md-font-icon-size});
    }
  }
}

.md-text-divider,
.md-floating-label {
  position: absolute;
}

.md-floating-label {
  @extend %md-secondary-text;

  cursor: text;
  pointer-events: none;
  top: $md-text-field-floating-label-top;
  transition-duration: $md-transition-time;
  transition-property: font-size, color, top;

  &.active {
    font-size: $md-text-field-active-font-size;
  }

  &.disabled {
    @extend %md-disabled-text;
  }
}

.md-text-divider {
  @extend %full-width;

  background: $md-border-color;
  height: 1px;

  &.from-left::after {
    left: 0;
  }

  &.from-right::after {
    margin-left: auto;
    right: 0;
  }

  &.from-center::after {
    margin: auto;
  }

  &::after {
    content: '';
    display: block;
    height: $md-text-field-divider-height;
    transition: width $md-transition-time;
    width: 0;
  }

  &.active::after {
    width: 100%;
  }
}

.md-text-field-icon {
  @extend %md-secondary-text;

  margin-right: $md-text-field-icon-margin;
  transition-duration: $md-transition-time;
  transition-property: color;

  &.disabled {
    @extend %md-disabled-text;
  }
}

.md-text-field-message {
  @extend %md-hint-text;

  display: flex;
  font-size: $md-text-field-info-font-size;
  justify-content: space-between;

