    }
  }
}
/// Themes the progress bars with the two colors.
///
/// @example scss - Basic Usage SASS
///   @include md-theme-progress($md-blue-500, $md-blue-300);
///
/// @example scss - Basic Usage CSS Output
///   .md-linear-progress-container {
///     background: #2196f3;
