    position: relative;
  }

  .md-slider-track--full-width {
    @extend %full-width;
  }

  .md-slider-track-fill {
    background: $md-slider-on-color;
    height: $md-slider-track-height;
    left: 0;
    margin: 0;
    position: absolute;
    top: 0;
    transition-duration: $md-transition-time;
    transition-property: width;
  }

  .md-slider-track-fill--dragging {
    transition-property: none;
  }

  .md-slider-thumb {
    border-radius: 50%;
    content: '';
    display: inline-block;
    height: $md-slider-thumb-size;
    position: absolute;
    top: -(($md-slider-thumb-size / 2) - ($md-slider-track-height / 2));
    transition-duration: $md-transition-time;
    transition-property: left, transform, background;
    width: $md-slider-thumb-size;
    z-index: $md-slider-thumb-z-index;

    &:focus {
      outline: none;
    }
  }

  .md-slider-thumb--on {
    background: $md-slider-on-color;
  }

  .md-slider-thumb--active {
    transform: scale($md-slider-thumb-active-scale);
    transform-origin: center;
  }

  .md-slider-thumb--dragging {
    transition-property: transform, background;
  }

  .md-slider-thumb--disabled {
    background: $md-slider-off-color;
    transform: scale($md-slider-thumb-disabled-scale);
  }

  .md-slider-thumb--continuous-off {
    background: $bg-color;
    border: 2px solid;
    border-color: $md-slider-off-color;
    position: relative;
  }

  .md-slider-thumb--mask {
    transform: scale(1);
    z-index: -1;
  }

  .md-slider-thumb--mask-inked {
    background: $md-slider-mask-ink-color;
    transform: scale(3.5);
    z-index: $md-slider-thumb-z-index - 1;
  }

  .md-slider-thumb--mask-disabled {
    background: $bg-color;
    border-radius: initial;
    transform: scale(1.1);
    z-index: $md-slider-thumb-z-index - 1;
  }

  @if $include-discrete {
    @include react-md-discrete-slider($primary-color, $light-theme);
  }

  @if $include-editor {
    @include react-md-editable-slider;
  }

  @if $include-indicators {
    @include react-md-slider-indicators;
  }
}

/// This will include the styles for a discrete Slider.
///
/// @param {Color} $primary-color [$md-primary-color] - The primary color to style the Slider with.
/// @param {Boolean} $light-theme [$md-light-theme] - A boolean if the slider should be styled for
///     the light or dark theme.
@mixin react-md-discrete-slider($primary-color: $md-primary-color, $light-theme: $md-light-theme) {
  $invalid-color: validate-slider-primary-color($primary-color, $light-theme);

  $md-slider-on-color: $primary-color;
  $md-slider-off-color: if($light-theme, $md-slider-light-theme-off-color, $md-slider-dark-theme-off-color);
  $bg-color: if($light-theme, $md-white-base, $md-dark-theme-background-color);
  $md-slider-discrete-mask-ink-color: rgba($primary-color, $md-slider-discrete-mask-ink-opacity);

  .md-slider-thumb--discrete {
    &::after {
      border-left: 7px solid transparent;
      border-right: 7px solid transparent;
      border-top: 7px solid transparent;
      border-top-left-radius: 10px;
      border-top-right-radius: 10px;
      content: '';
      height: 0;
      left: 0;
      position: absolute;
      top: 10px;
      transition-duration: $md-transition-time;
      transition-property: border-top-color, transform;
      width: 0;
    }
  }

  .md-slider-thumb--discrete-active {
    top: 0;
    transform: scale(2) translate3d(0, -18px, 0);

  }

  .md-sider-thumb--discrete-on {
    &::after {
      border-top-color: $md-slider-on-color;
    }
  }

  .md-slider-thumb--discrete-off {
    background: if($light-theme, $md-black-base, $md-white-base);
  }

  .md-slider-thumb--discrete-active-off {
    background: $md-grey-400;

    &::after {
      border-top-color: $md-grey-400;
    }
  }

  .md-slider-thumb--discrete-mask-inked {
    transform: scale(2.5);
    transition-duration: $md-slider-discrete-ink-transition-time;
  }

  .md-slider-thumb--discrete-mask-leaving {
    background: $md-slider-discrete-mask-ink-color;
    transition-duration: $md-slider-discrete-ink-transition-time;
    z-index: $md-slider-thumb-z-index - 1;
  }

  .md-slider-discrete-value {
    align-items: center;
    color: $md-white-base;
    display: flex;
    font-size: 12px;
    height: $md-slider-thumb-size * 2;
    justify-content: center;
    position: absolute;
    text-align: center;
    transform: translate3d(-7px, -43px, 0);
    transition-duration: $md-transition-time;
    transition-property: left;
    width: $md-slider-thumb-size * 2;
    z-index: $md-slider-thumb-z-index + 1;
  }

  .md-slider-discrete-tick {
    background: if($light-theme, $md-black-base, $md-white-base);
    content: '';
    height: $md-slider-discrete-tick-height;
    position: absolute;
    top: -(($md-slider-discrete-tick-height / 2) - ($md-slider-track-height / 2));
    z-index: $md-slider-thumb-z-index - 1;
  }

  .md-slider-discrete-value--dragging {
    transition-property: none;
  }
}

/// This includes the small scss for displaying an editable slider.
@mixin react-md-editable-slider {
  .md-slider-editor {
    text-align: right;
  }

  .md-slider-container {
    .md-text-field-container {
      margin-left: 4px;
      padding-right: 0;
    }
  }
}

/// This includes the small scss for displaying `.md-icon` or indicators with a Slider.
@mixin react-md-slider-indicators {
  .md-slider-container {
    .md-icon {
      $margin: ($md-slider-height - $md-font-icon-size) / 2;

      margin-bottom: $margin;
      margin-top: $margin;
    }
  }

  .md-slider-ind {
    display: block;
    text-align: center;
  }

  .md-slider-track--ind-left {
    margin-left: $md-default-padding;
  }

  .md-slider-track--ind-right {
    margin-right: $md-default-padding;
  }

  @media only screen and (min-width: $md-mobile-min-width) {
    .md-slider-ind {
      font-size: $md-text-field-mobile-font-size;
      margin-top: 14px;
    }
  }

  @media only screen and (min-width: $md-desktop-min-width) {
    .md-slider-ind {
      font-size: $md-text-field-desktop-font-size;
      margin-top: 16px;
    }
  }
}


/// This will theme the Slider component with an updated `primary-color` and
/// optionally a switch in themes.
///
/// @param {Color} $primary-color [$md-primary-color] - The primary color to use for the
///     slider. This will be the non-disabled track's fill color and the non-disabled thumb's
///     color. If you are using the `dark-theme`, This value should be the primary color with
///     a `-200` suffix.
/// @param {Boolean} $light-theme [$md-light-theme] - A boolean if the application using the
///     light theme.
/// @param {Boolean} $include-discrete [true] - A boolean if the discrete Slider scss should be
///     themed as well.
@mixin react-md-theme-sliders($primary-color: $md-primary-color, $light-theme: $md-light-theme, $include-discrete: true) {
  $invalid-color: validate-slider-primary-color($primary-color, $light-theme);

  $md-slider-on-color: $primary-color;
  $md-slider-off-color: if($light-theme, $md-slider-light-theme-off-color, $md-slider-dark-theme-off-color);
  $bg-color: if($light-theme, $md-white-base, $md-dark-theme-background-color);

  .md-slider-thumb--on,
  .md-slider-track-fill {
    background: $md-slider-on-color;
  }

  @if $light-theme != $md-light-theme {
    .md-slider-track,
    .md-slider-thumb--disabled {
      background: $md-slider-off-color;
    }

    .md-slider-thumb--continuous-off,
    .md-slider-thumb--disabled-mask {
      background: $bg-color;
    }

    .md-slider-thumb--off {
      border-color: $md-slider-off-color;
    }
  }

  @if $include-discrete {
    @include react-md-theme-discrete-slider($primary-color);
  }
}

/// This will update the discrete Slider with the new `$primary-color`.
///
/// @param {Color} $primary-color [$md-primary-color] - The primary color to
///     theme the discrete Slider with.
/// @param {Boolean} $light-theme [$md-light-theme] - Boolean if the discrete Slider
///     should be themed for the light or dark theme.
@mixin react-md-theme-discrete-slider($primary-color: $md-primary-color, $light-theme: $md-light-theme) {
  $invalid-color: validate-slider-primary-color($primary-color, $light-theme);

  $md-slider-on-color: $primary-color;

  .md-slider-thumb--discrete-on {
    &::after {
      border-top-color: $md-slider-on-color;
    }
  }
}

/// A helper function to validate that a `$primary-color` has a `-200` suffix when
/// using the dark theme. It doesn't actually output anything with sass-loader atm
/// though. :/
///
/// @param {Color} $primary-color - The primary color to validate.
/// @param {Boolean} $light-theme - Boolean if the current theme is the light theme.
/// @return {Boolean} true if the primary color is valid or false if it is invalid.
@function validate-slider-primary-color($primary-color, $light-theme) {
  @if $light-theme {
    @return true;
  }

  $found: false;
  @each $name, $color in $md-color-map {
    @if str-index($name, '-200') != null && str-index($name, '-a-200') == null && $color == $primary-color {
      @return true;
    }
  }

  @warn "The current primary-color: '#{$primary-color}' is not a valid '-200' suffix. The primary color must be a non-accent -200 color when using the dark theme sliders.";

  @return false;
}
@mixin md-theme-subheaders($color) {
  .md-subheader.md-primary {
    color: $color;
  }
}
/// Themes the selection controls to use the given color
///
/// Note: If this is the dark theme, the $color param should be a -200 suffix.
///
/// @param {String} $color - The color to style with
/// @param {Boolean} $light-theme [$md-light-theme] - If it should be styled using the light theme
/// @group selection-controls
@mixin md-theme-selection-controls($color, $light-theme: $md-light-theme) {
  .md-radio,
  .md-checkbox {
    &.active {
      color: $color;
    }
  }

  .md-control-container.disabled {
    color: if($light-theme, $md-light-theme-disabled-text-color, $md-dark-theme-disabled-text-color);

    .md-switch {
      background: if($light-theme, $md-switch-light-theme-disabled-color, $md-switch-dark-theme-disabled-color);

      &::before {
        background: if($light-theme, $md-grey-400, $md-grey-800);
      }
    }
  }

  .md-control-input:checked + .md-switch {
    background: rgba($color, .5);
  }

  .md-control-input:checked +.md-switch::before {
    background: $color;
    border-color: $color;
  }

  .md-switch {
    background: if($light-theme, $md-switch-light-theme-track-color, $md-switch-dark-theme-track-color);

    &::before {
      background: if($light-theme, $md-grey-50, $md-grey-400);
    }
  }
}
/// Themes most of the components with the given colors.
/// Avatars are excluded from this mixin.
///
/// @param {Color} $primary [$md-primary-color] - The primary color to theme with
/// @param {Color} $primary-hue-1 [$md-primary-color-hue-1] - A hue color to use
///       for date pickers
/// @param {Color} $secondary [$md-secondary-color] - The secondary color to use
/// @param {Color} $secondary-hue-1 [$md-secondary-color-hue-1] - A hue color to use
///       for the progress bar at an inactive state.
/// @group colors
@mixin md-theme-most($primary: $md-primary-color, $primary-hue-1: $md-primary-color-hue-1, $secondary: $md-secondary-color, $secondary-hue-1: $md-secondary-color-hue-1) {
  @include md-theme-bottom-navigations($primary);
  @include md-theme-navigation-drawers($primary);
  @include md-theme-text-fields($primary);
  @include md-theme-snackbars($primary);
  @include md-theme-pickers($primary, $primary-hue-1);
  @include md-theme-lists($primary);
  @include md-theme-progress($secondary, $secondary-hue-1);
  @include react-md-theme-sliders($primary);
  @include md-theme-subheaders($primary);
  @include md-theme-selection-controls($secondary);

  .md-primary {
    @include md-theme-toolbars($primary, $md-white-base);
    @include md-theme-buttons($primary);
    @include md-theme-tabs($primary, $secondary);
  }

  .md-secondary {
    @include md-theme-buttons($secondary);
  }
}
/// Themes the material design buttons with the given color
///
/// @example scss - Example Basic Usage SASS
///   .md-primary {
///     @include md-theme-buttons($md-primary-color);
///   }
///
/// @example scss - Example Basic Usage CSS Output
///   .md-primary.md-raised-btn,
///   .md-primary.md-floating-btn {
///     background: #3f51b5;
///     color: #fff;
///   }
///
///   .md-primary.md-raised-btn:hover,
///   .md-primary.md-floating-btn:hover {
///     background: rgba(63, 81, 181, 0.95);
///   }
///
///   .md-primary.md-raised-btn .md-ink,
///   .md-primary.md-floating-btn .md-ink {
///     background: rgba(255, 255, 255, 0.05);
///   }
///
///   .md-primary.md-flat-btn {
///     color: #ef51b5;
///   }
///
///   .md-primary.md-flat-btn:hover {
///     background: rgba(63, 81, 181, 0.15);
///   }
///
///   .md-primary.md-flat-btn .md-ink {
///     background: rgba(63, 81, 181, 0.22);
///   }
///
/// @param {Color} $color - The background color to use for raised and floating buttons
///                         and the text color to use for flat buttons.
/// @param {Color} $text-color [$md-white-base] - The text color for raised and floating buttons.
/// @param {Color} $hover-color [rgba($color, .95)] - The hover color for raised and floating buttons.
///        If this value is set to null, it will use the default listed.
/// @param {Color} $ink-color [rgba($md-white-base, .05)] - The ink color for raised and floating buttons.
///        If this value is set to null, it will use the default listed.
/// @param {Color} $flat-hover-color [rgba($color, .15)] - The hover color for flat buttons.
///        If this value is set to null, it will use the default listed.
/// @param {Color} $flat-ink-color [rgba($color, .22)] - The ink color for flat buttons.
///        If this value is set to null, it will use the default listed.
/// @group buttons
@mixin md-theme-buttons($color, $text-color: $md-white-base, $hover-color: null, $ink-color: null, $flat-hover-color: null, $flat-ink-color: null) {
  $hover-color: if($hover-color == null, rgba($color, .95), $hover-color);
  $ink-color: if($ink-color == null, rgba($md-white-base, .2), $ink-color);
  $flat-hover-color: if($flat-hover-color == null, rgba($color, .15), $flat-hover-color);
  $flat-ink-color: if($flat-ink-color == null, rgba($color, .22), $flat-ink-color);

  &.md-raised-btn:not([disabled]),
  &.md-floating-btn:not([disabled]) {
    background: $color;
    color: $text-color;

    &:hover {
      background: $hover-color;
    }

    .md-ink {
      background: $ink-color;
    }
  }

  &.md-flat-btn:not([disabled]) {
    color: $color;

    &:hover {
      background: $flat-hover-color;
    }

    .md-ink {
      background: $flat-ink-color;
    }
  }
}


@mixin md-buttons-desktop() {

  .md-btn:hover:not([disabled]) {
    cursor: pointer;
  }

  .md-raised-btn,
  .md-floating-btn,
  .md-flat-btn {
    &:hover {
      background: $md-btn-hover-color;
    }
  }
}
/// Themes the tabs with the given colors
/// @example scss - Example Basic Usage SASS
///   .md-primary {
///     $primary: $md-cyan-500;
///     $secondary: $md-yellow-a-200;
///     @include md-theme-tabs($primary, $secondary);
///    }
///
/// @example scss - Example Basic Usage CSS Output
///   .md-primary.md-tabs-scroll-container {
///     background: #00bcd4;
///   }
///
///   .md-primary.md-tabs-scroll-container .md-tab {
///     color: rgba(#fff, .7);
///   }
///
///  .md-primary.md-tabs-scroll-container .md-tab.active {
///    color: #fff;
///  }
///
///  .md-primary.md-tabs-scroll-container .md-tab-indicator {
///    background: #ffea00;
///  }
///
///  .md-primary.md-tabs-scroll-container .md-ink {
///    background: rgba(#fff, .15);
///  }
///
/// @param {String} $primary-color - The primary color to use for a tab
/// @param {String} $secondary-color - The secondary color to use for a tab
/// @group tabs
@mixin md-theme-tabs($primary-color, $secondary-color) {
  &.md-tabs-scroll-container {
    background: $primary-color;

    .md-tab {
      color: $md-dark-theme-secondary-text-color;

      &.active {
        color: $md-dark-theme-text-color;
      }
    }

    .md-tab-indicator {
      background: $secondary-color;
    }
  }
}

/// Adds additional css for the material design tabs for a mobile device
///
/// @example scss - Example Usage SASS
///   @media only screen and (min-width: 0px) and (max-width: 599px) {
///     @include md-tab-mobile();
///   }
///
/// @group tabs
@mixin md-tab-mobile() {
  .md-tab {
    font-size: $md-tab-mobile-font-size;
    min-width: $md-tab-mobile-min-width;
  }
}

/// Adds additional css for the material design tabs for a desktop
///
/// @example scss - Example Usage SASS
///   @media only screen and (min-width: 600px) {
///     @include md-tab-desktop();
///   }
///
/// @group tabs
@mixin md-tab-desktop() {
  .md-tab {
    font-size: $md-tab-desktop-font-size;
    min-width: $md-tab-desktop-min-width;
  }

  .md-tab-label {
    padding: $md-tab-desktop-padding;
  }
}
/// Themes the avatar with the given colors
/// @example scss - Basic Usage SASS
///   @include md-theme-avatars-suffix($md-indigo-500, $md-white-base, color-1);
///
/// @example scss - Basic Usage CSS Output
///   .md-avatar-color-1 {
///     background: #3f51b5;
///     color: #fff;
///   }
///
/// @param {String} $bg-color - The background color to use for the avatar
/// @param {String} $text-color - The text color to use in the avatar
/// @param {String} $suffix - The class name suffix to use. Will be added to the end of .md-avatar
/// @group avatars
@mixin md-theme-avatars-suffix($bg-color, $text-color, $suffix) {
  .md-avatar.md-avatar-#{$suffix} {
    background: $bg-color;
    color: $text-color;
  }
}
@import 'helpers/variables';

$md-mobile-max-width: $md-desktop-min-width - 1;

@media only screen and (min-width: #{$md-mobile-min-width}) and (max-width: #{$md-mobile-max-width}) {
  @include md-typography-mobile();
  @include md-lists-mobile();
  @include md-select-field-mobile();
  @include md-sidebar-mobile();
  @include md-snackbar-mobile();
  @include md-tab-mobile();
  @include md-text-field-mobile();
  @include md-tooltips-mobile();
  @include md-navigation-drawer-mobile();
}

@media only screen and (min-width: #{$md-desktop-min-width}) {
  @include md-typography-desktop();
  @include md-navigation-drawer-desktop();
  @include md-buttons-desktop();
  @include md-lists-desktop();
  @include md-select-field-desktop();
  @include md-sidebar-desktop();
  @include md-snackbar-desktop();
  @include md-tab-desktop();
  @include md-text-field-desktop();
  @include md-toolbar-desktop();
  @include md-tooltips-desktop();

  .md-picker:not(.portrait) {
    @include md-pickers-landscape();
  }
}

@media only screen and (orientation: portrait) and (min-width: #{$md-mobile-min-width}) and (max-width: #{$md-mobile-max-width}) {
  @include md-toolbar-mobile-portrait();
  @include md-navigation-drawer-mobile-portrait();

  .md-picker:not(.landscape) {
    @include md-pickers-portrait();
  }
}

@media only screen and (orientation: landscape) and (min-width: #{$md-mobile-min-width}) and (max-width: #{$md-mobile-max-width}) {
  @include md-toolbar-mobile-landscape();
  @include md-navigation-drawer-mobile-landscape();

  .md-picker:not(.portrait) {
    @include md-pickers-landscape();
  }
}

@media only screen and (orientation: landscape) and (min-width: #{$md-tablet-min-width}) and (max-width: #{$md-mobile-max-width}) {
  @include md-navigation-drawer-tablet-landscape();
}
/// Updates the typography for mobile devices.
///
/// @group typography
@mixin md-typography-mobile() {
  [class^='md-subheading'] {
    font-size: $md-subheading-mobile-font-size;
  }

  .md-body-1,
  .md-body-2 {
    font-size: $md-body-mobile-font-size;
  }
}


/// Updates the typography for desktop screens.
///
/// @group typography
@mixin md-typography-desktop() {
  [class^='md-subheading'] {
    font-size: $md-subheading-desktop-font-size;
  }

  .md-body-1,
  .md-body-2 {
    font-size: $md-body-desktop-font-size;
  }
}
/// Adds additional css to the select field for mobile devices
///
/// @example scss - Example Usage SASS
///   @media only screen and (min-width: 0px) and (max-width: 599px) {
///     @include md-select-field-mobile();
///   }
///
/// @group select-fields
@mixin md-select-field-mobile() {
  .md-menu.md-select-field-menu:not(.md-transition-below) {
    top: $md-select-field-mobile-top;

    &.single-line {
      top: -$md-list-mobile-top-padding;
    }
  }
}

/// Adds additional css to the select field for desktop
///
/// @example scss - Example Usage SASS
///   @media only screen and (min-width: 600px) {
///     @include md-select-field-desktop();
///   }
///
/// @group select-fields
@mixin md-select-field-desktop() {
  .md-menu.md-select-field-menu:not(.md-transition-below) {
    top: $md-select-field-desktop-top;

    &.single-line {
      top: -$md-list-desktop-top-padding;
    }
  }

  .md-toolbar,
  .md-drawer-header {
    .md-select-field-container.select-field-btn {
      height: $md-toolbar-desktop-height;
      padding-bottom: 12px;
      padding-top: 12px;
    }
  }
}
/// Updates the sidebar to be styled for mobile devices.
///
/// @group sidebars
@mixin md-sidebar-mobile() {
  .md-sidebar {
    max-width: $md-sidebar-mobile-max-width;

    &.active {
      width: calc(100vw - 56px);
    }
  }

  .md-sidebar,
  .md-sidebar-container.responsive {
    height: 100vh;
    position: fixed;
  }

  .md-toolbar-container.fixed ~ .md-sidebar-container.responsive {
    padding-top: 0;
  }

  .md-sidebar-container.responsive {
    z-index: $md-sidebar-fixed-z-index;
  }
}

/// Updates the sidebar to be styled for desktop screens.
///
/// @group sidebars
@mixin md-sidebar-desktop() {
  .md-sidebar-container.responsive {
    position: fixed;

    .md-overlay {
      display: none;
      visibility: hidden;
    }
  }

  .md-toolbar-container ~ .md-sidebar-container .md-sidebar {
    margin-top: $md-toolbar-height;
    max-height: calc(100vh - #{$md-toolbar-height});
  }

  .md-sidebar {
    &.md-sidebar-left {
      max-width: $md-sidebar-desktop-max-width;
    }

    &.active {
      width: $md-sidebar-open-width;
    }
  }
}
/// Additional styling for tooltips on mobile devices.
///
/// @group tooltips
@mixin md-tooltips-mobile() {
  .md-tooltip {
    height: $md-tooltip-mobile-height;
    padding-left: $md-tooltip-mobile-lr-padding;
    padding-right: $md-tooltip-mobile-lr-padding;
  }

  .md-tooltip-text {
    font-size: $md-tooltip-mobile-font-size;
    line-height: $md-tooltip-mobile-height;
  }
}

/// Additional styling for tooltips on desktop screens.
///
/// @group tooltips
@mixin md-tooltips-desktop() {
  .md-tooltip {
    height: $md-tooltip-desktop-height;
    padding-left: $md-tooltip-desktop-lr-padding;
    padding-right: $md-tooltip-desktop-lr-padding;
  }

  .md-tooltip-text {
    font-size: $md-tooltip-desktop-font-size;
    line-height: $md-tooltip-desktop-height;
  }
}
/// Themes the app bar to the given colors
///
/// @example scss- Example Basic Usage SASS
///   .md-primary {
///     @include md-theme-toolbar($md-primary-color);
///   }
///
/// @example scss - Example Basic Usage CSS Output
///   .md-primary.md-toolbar {
///     background: #3f51b5;
///     color: #fff;
///    }
///
/// @param {Color} $color - The background colro for the app bar
/// @param {Color} $text-color - The text color to use in the app bar
/// @group toolbars
@mixin md-theme-toolbars($color, $text-color) {
  &.md-toolbar {
    background: $color;
    color: $text-color;
  }
}

@mixin md-toolbar-mobile-landscape() {
  .md-toolbar,
  .md-navigation-drawer-toolbar .md-text-field:not(.multi-line),
  .md-toolbar .md-text-field:not(.multi-line) {
    height: $md-toolbar-mobile-landscape-height;
  }

  .md-navigation-drawer-toolbar .md-text-field:not(.multi-line),
  .md-toolbar .md-text-field:not(.multi-line) {
    line-height: $md-toolbar-mobile-landscape-height;
  }
}

@mixin md-toolbar-mobile-portrait() {
  .md-toolbar,
  .md-navigation-drawer-toolbar .md-text-field:not(.multi-line),
  .md-toolbar .md-text-field:not(.multi-line) {
    height: $md-toolbar-mobile-portrait-height;
  }

  .md-navigation-drawer-toolbar .md-text-field:not(.multi-line),
  .md-toolbar .md-text-field:not(.multi-line) {
    line-height: $md-toolbar-mobile-portrait-height;
  }
}

/// Adds additional css to the app bar for desktop
///
/// @group toolbars
@mixin md-toolbar-desktop() {
  .md-toolbar,
  .md-navigation-drawer-toolbar .md-text-field:not(.multi-line),
  .md-toolbar .md-text-field:not(.multi-line) {
    height: $md-toolbar-desktop-height;
  }

  .md-navigation-drawer-toolbar .md-text-field:not(.multi-line),
  .md-toolbar .md-text-field:not(.multi-line) {
    line-height: $md-toolbar-desktop-height;
  }
}
// ////////////////////////////////////////////////////// //
// BEM Sass port of Flexbox Grid - http://flexboxgrid.com //
// ////////////////////////////////////////////////////// //

// Variables
$grid-columns: 12 !default;

$grid-gutter: 2rem !default;

$grid-breakpoints: (
  xs: false,
  sm: 48em,
  md: 62em,
  lg: 75em
) !default;

$grid-containers: (
  xs: false,
  sm: 46rem,
  md: 61rem,
  lg: 71rem,
) !default;

// Mixins
@mixin breakpoint($breakpoint, $breakpoint-size) {
  // The grid is mobile first so xs is the default, hence no breakpoint
  @if $breakpoint == xs {
    @content;
  }

  // Add breakpoints for larger screens
  @else {
    @media only screen and (min-width: $breakpoint-size) {
      // Static grid
      .grid {
        width: map-get($grid-containers, $breakpoint);
      }

      @content;
    }
  }
}

// Grid
.grid,
.grid--fluid {
  margin: 0 auto;
}

.grid--fluid {
  padding-right: $grid-gutter;
  padding-left: $grid-gutter;
}

// Rows
.grid__row {
  box-sizing: border-box;
  display: flex;
  flex: 0 1 auto;
  flex-direction: row;
  flex-wrap: wrap;
  margin-right: ($grid-gutter / -4);
  margin-left: ($grid-gutter / -4);

  &--reverse {
    flex-direction: row-reverse;
  }
}

// Columns
.grid__col {
  &--reverse {
    flex-direction: column-reverse;
  }
}

@each $breakpoint, $breakpoint-size in $grid-breakpoints {
  @include breakpoint($breakpoint, $breakpoint-size) {
    $col-selectors: (".grid__col--#{$breakpoint}");

    @for $col from 1 through $grid-columns {
      $col-selector: unquote(".grid__col--#{$breakpoint}-#{$col}");
      $col-selectors: append($col-selectors, $col-selector, comma);
    }

    @for $col from 1 through $grid-columns {
      $col-offset-selector: unquote(".grid__col--#{$breakpoint}-offset-#{$col}");
      $col-selectors: append($col-selectors, $col-offset-selector, comma);
    }

    #{$col-selectors} {
      box-sizing: border-box;
      flex: 0 0 auto;
      padding-right: ($grid-gutter / 4);
      padding-left: ($grid-gutter / 4);
    }

    .grid__col {
      &--#{$breakpoint} {
        flex-grow: 1;
        flex-basis: 0;
        max-width: 100%;

        @for $col from 1 through $grid-columns {
          &-#{$col} {
            flex-basis: (100% / $grid-columns) * $col;
            max-width: (100% / $grid-columns) * $col;
          }
        }

        @for $col from 1 through $grid-columns {
          &-offset-#{$col} {
            margin-left: (100% / $grid-columns) * $col;
          }
        }
      }
    }

    // Utilities
    .grid {
      &__row {
        &--#{$breakpoint}-start {
          justify-content: flex-start;
          text-align: start;
        }

        &--#{$breakpoint}-center {
          justify-content: center;
          text-align: center;
        }

        &--#{$breakpoint}-end {
          justify-content: flex-end;
          text-align: end;
        }

        &--#{$breakpoint}-top {
          align-items: flex-start;
        }

        &--#{$breakpoint}-middle {
          align-items: center;
        }

        &--#{$breakpoint}-bottom {
          align-items: flex-end;
        }

        &--#{$breakpoint}-around {
          justify-content: space-around;
        }

        &--#{$breakpoint}-between {
          justify-content: space-between;
        }
      }

      &__col {
        &--#{$breakpoint}-first {
          order: -1;
        }

        &--#{$breakpoint}-last {
          order: 1;
        }
      }
    }
  }
}
.postimage__wrap {
  width: 775px;
  height: 300px;
}

.postimage {
  object-fit: cover;
  transform: translate3d(0, -5px, 0);
  transition: opacity .35s, transform .35s;
  max-height: 300px;
  width: 100%;

  &:hover {
    opacity: .7;
    transform: translate3d(0, 0, 0);
  }
}
.hero {
  height: 400px;
  background-color: $grape;

  color: $snow;
}
.md-text-field {
  width: 100%;
}

.form__navigation {
  padding: 1em;
  overflow: hidden;
}

.form__footer {
  margin-top: 35px;
  padding-top: 25px;
}

.card__form {
  padding: 1em;
}
